import React from 'react';
import File from './File';
import axios from 'axios';

//const filesList = ['myFile.txt', 'kjsdf xg .txt']

function Explorer() {
  const [active, setActive] = React.useState('')
  const [filesList, setFilesList] = React.useState([])


  React.useEffect( () => {
    axios.get('http://localhost:1200/files').then(res => setFilesList(res.data))
    .catch(err => console.log(err))
  }, [])

  function onFileClicked(fileName){
    const file = fileName.split('.').slice(0, -1).join('.')
    console.log(file)
    axios.get(`http://localhost:1200/files/${file}`)
    .then(res =>  setActive(res.data))
    .catch(err => console.log(err))
  }
 
  return <div className='explorer'>
    <div style={{ width: '100%' }}>
      {filesList.map(f => <File key={f} name={f} onClick={() => onFileClicked(f)} />)}
    </div>
    <aside className={`info ${active ? 'open' : ''}`}>
      <h3>Info</h3>
      <label></label>
      <div className='content'>
      {
        
          active.content
        } 
          
       
      
      </div>
      <h3>Stats</h3>
      <div>
        
    <label>size:</label><span></span><br />
        <label>size:</label><span>21kb</span><br />
      </div>
    </aside>
  </div>
}

export default Explorer
